import React, { useState } from 'react';

const App = ()=>{
let newtime = new Date().toLocaleTimeString();
const [ctime, setNtime]=useState(newtime);

const UpdateTime=()=>{
    newtime=new Date().toLocaleTimeString();
    setNtime(newtime);
}


    return(
    <>
   <h1>{ctime}</h1>
   <button onClick={UpdateTime} >Get New Time</button>
   <h5 >By Sandeep</h5>
    </>
    );
}
export default App;
